mapper.update:
UPDATE BBS
   SET BBS_HIT = BBS_HIT + 1
 WHERE BBS_NO = #{bbsNo}

mapper.select:
SELECT BBS_NO, BBS_TITLE, BBS_CONTENT, BBS_IP, BBS_CREATE_DATE, BBS_HIT
  FROM BBS
 WHERE BBS_NO = #{bbsNo}

mapper.update:
UPDATE BBS
   SET BBS_TITLE = #{bbsTitle}
     , BBS_CONTENT = #{bbsContent}
 WHERE BBS_NO = #{bbsNo}
